import React, { Fragment, useState, useRef } from 'react';
import { Dimensions, Linking, Modal, PermissionsAndroid, Platform } from 'react-native';
import { useIntl } from 'react-intl';
import CameraRoll from '@react-native-community/cameraroll';
import RNFetchBlob from 'rn-fetch-blob';
import ImageViewer from 'react-native-image-zoom-viewer';
import ActionSheet from 'react-native-actionsheet';
import { connect } from 'react-redux';

import AutoHeightWebView from 'react-native-autoheight-webview';
import EStyleSheet from 'react-native-extended-stylesheet';
import get from 'lodash/get';
import { navigate } from '../../../../navigation/service';

// Constants
import { default as ROUTES } from '../../../../constants/routeNames';

import { CommentPlaceHolder } from '../../../basicUIElements';
import { customCommentScript } from './config';

import { TextButton } from '../../..';

// Styles
import styles from './postBodyStyles';

// Services and Actions
import { writeToClipboard } from '../../../../utils/clipboard';
import { toastNotification } from '../../../../redux/actions/uiAction';

const WIDTH = Dimensions.get('window').width;

const CommentBody = ({
  body,
  textSelectable = true,
  handleOnUserPress,
  handleOnPostPress,
  handleOnLongPress,
  created,
  commentDepth,
  reputation,
  dispatch,
}) => {
  const [isImageModalOpen, setIsImageModalOpen] = useState(false);
  const [postImages, setPostImages] = useState([]);
  const [selectedImage, setSelectedImage] = useState(null);
  const [selectedLink, setSelectedLink] = useState(null);

  const [revealComment, setRevealComment] = useState(reputation > 0);
  const intl = useIntl();
  const actionImage = useRef(null);
  const actionLink = useRef(null);

  const _showLowComment = () => {
    setRevealComment(true);
  };
  //new renderer functions
  const __handleOnLinkPress = (event) => {
    if ((!event && !get(event, 'nativeEvent.data'), false)) {
      return;
    }
    try {
      let data = {};
      try {
        data = JSON.parse(get(event, 'nativeEvent.data'));
      } catch (error) {
        data = {};
      }

      const {
        type,
        href,
        images,
        image,
        author,
        category,
        permlink,
        tag,
        proposal,
        videoHref,
      } = data;

      switch (type) {
        case '_external':
        case 'markdown-external-link':
          setSelectedLink(href);
          actionLink.current.show();
          break;
        case 'longpress':
          handleOnLongPress();
          break;
        case 'markdown-author-link':
          if (!handleOnUserPress) {
            __handleOnUserPress(author);
          } else {
            handleOnUserPress(author);
          }
          break;
        case 'markdown-post-link':
          if (!handleOnPostPress) {
            __handleOnPostPress(permlink, author);
          } else {
            handleOnPostPress(permlink, author);
          }
          break;
        case 'markdown-tag-link':
          __handleTagPress(tag);
          break;
        case 'markdown-witnesses-link':
          break;
        case 'markdown-proposal-link':
          break;
        case 'markdown-video-link':
          break;
        case 'image':
          setPostImages(images);
          setSelectedImage(image);
          actionImage.current.show();
          break;

        default:
          break;
      }
    } catch (error) {}
  };

  const handleImagePress = (ind) => {
    if (ind === 1) {
      //open gallery mode
      setIsImageModalOpen(true);
    }
    if (ind === 0) {
      //copy to clipboard
      writeToClipboard(selectedImage).then(() => {
        dispatch(
          toastNotification(
            intl.formatMessage({
              id: 'alert.copied',
            }),
          ),
        );
      });
    }
    if (ind === 2) {
      //save to local
      _saveImage(selectedImage);
    }
  };

  const handleLinkPress = (ind) => {
    if (ind === 1) {
      //open link
      Linking.canOpenURL(selectedLink).then((supported) => {
        if (supported) {
          Linking.openURL(selectedLink);
        } else {
          dispatch(
            toastNotification(
              intl.formatMessage({
                id: 'alert.failed_to_open',
              }),
            ),
          );
        }
      });
    }
    if (ind === 0) {
      //copy to clipboard
      writeToClipboard(selectedLink).then(() => {
        dispatch(
          toastNotification(
            intl.formatMessage({
              id: 'alert.copied',
            }),
          ),
        );
      });
    }
  };

  const __handleTagPress = (tag) => {
    if (tag) {
      navigate({
        routeName: ROUTES.SCREENS.TAG_RESULT,
        params: {
          tag,
        },
      });
    }
  };

  const __handleOnPostPress = (permlink, author) => {
    if (permlink) {
      navigate({
        routeName: ROUTES.SCREENS.POST,
        params: {
          author,
          permlink,
        },
        key: permlink,
      });
    }
  };

  const __handleOnUserPress = (username) => {
    if (username) {
      navigate({
        routeName: ROUTES.SCREENS.PROFILE,
        params: {
          username,
        },
        key: username,
      });
    } else {
      dispatch(
        toastNotification(
          intl.formatMessage({
            id: 'post.wrong_link',
          }),
        ),
      );
    }
  };

  const checkAndroidPermission = async () => {
    try {
      const permission = PermissionsAndroid.PERMISSIONS.WRITE_EXTERNAL_STORAGE;
      await PermissionsAndroid.request(permission);
      Promise.resolve();
    } catch (error) {
      Promise.reject(error);
    }
  };

  const _downloadImage = async (uri) => {
    return RNFetchBlob.config({
      fileCache: true,
      appendExt: 'jpg',
    })
      .fetch('GET', uri)
      .then((res) => {
        let status = res.info().status;

        if (status == 200) {
          return res.path();
        } else {
          Promise.reject();
        }
      })
      .catch((errorMessage) => {
        Promise.reject(errorMessage);
      });
  };

  const _saveImage = async (uri) => {
    try {
      if (Platform.OS === 'android') {
        await checkAndroidPermission();
        uri = `file://${await _downloadImage(uri)}`;
      }
      CameraRoll.saveToCameraRoll(uri)
        .then((res) => {
          dispatch(
            toastNotification(
              intl.formatMessage({
                id: 'post.image_saved',
              }),
            ),
          );
        })
        .catch((error) => {
          dispatch(
            toastNotification(
              intl.formatMessage({
                id: 'post.image_saved_error',
              }),
            ),
          );
        });
    } catch (error) {
      dispatch(
        toastNotification(
          intl.formatMessage({
            id: 'post.image_saved_error',
          }),
        ),
      );
    }
  };

  const html = body.replace(/<a/g, '<a target="_blank"');
  const customStyle = `
  * {
    color: ${EStyleSheet.value('$primaryBlack')};
    font-family: Roboto, sans-serif;
    max-width: 100%;

    overflow-wrap: break-word;
    word-wrap: break-word;

    -ms-word-break: break-all;
    word-break: break-all;
    word-break: break-word;

    -ms-hyphens: auto;
    -moz-hyphens: auto;
    -webkit-hyphens: auto;
    hyphens: auto;

  }
  body {
    color: ${EStyleSheet.value('$primaryBlack')};
  }
  a {
    color: ${EStyleSheet.value('$primaryBlue')};
    cursor: pointer; 
  }
  img {
    align-self: 'center';
    max-width: 100%;
  }
  center {
    text-align: 'center';
    align-items: 'center';
    justify-content: 'center';
  }
  table {
    table-layout: fixed;
    width: 100%;
  }
  th {
    flex: 1;
    justify-content: 'center';
    font-weight: 'bold';
    color: ${EStyleSheet.value('$primaryBlack')};
    font-size: 14;
    padding: 5;
  }
  tr {
    background-color: ${EStyleSheet.value('$darkIconColor')};
    flex-direction: 'row';
  }
  td {
    border-width: 0.5;
    border-color: ${EStyleSheet.value('$tableBorderColor')};
    flex: 1;
    padding: 10;
    background-color: ${EStyleSheet.value('$tableTrColor')};
  }
  blockquote {
    border-left-width: 5;
    border-left-style: solid;
    border-color: ${EStyleSheet.value('$darkIconColor')};
    margin-left: 5;
    padding-left: 5;
  }
  code {
    background-color: ${EStyleSheet.value('$darkIconColor')};
    font-family: ${EStyleSheet.value('$editorFont')};
  }
  center {
    text-align: 'center';
    align-items: 'center';
    justify-content: 'center';
  }
  .markdown-video-link {
    max-width: 100%;
    position: relative;
  }
  .markdown-video-play {
    position: absolute;
    width: 100px;
    height: 100px;
    background: url('data:image/png;base64,') no-repeat center center;
    z-index: 20;
    opacity: 0.9;
    left: 50%;
    top: 50%;
    margin-top: -100px;
    transform: translateX(-50%) translateY(-50%);
    -webkit-transform: translateX(-50%) translateY(-50%);
    -moz-transform: translateX(-50%) translateY(-50%);
  }
  b > * {
    display: inline-block;
    margin: 2 10;
  }
  iframe {
    width: 100%;
    height: 240px;
  }
  .pull-right {
    float: right;
  }
  .pull-left {
    float: left;
  }
  .pull-left,
  .pull-right {
    max-width: calc(50% - 10px);
    padding-left: 10px;
    margin-bottom: 10px;
    box-sizing: border-box;
  }
  .phishy {
    display: inline;
    color: red;
  }
  .text-justify {
    text-align: justify;
    text-justify: inter-word;
    letter-spacing: 0px;
  }
  `;
  return (
    <Fragment>
      <Modal key={`mkey-${created.toString()}`} visible={isImageModalOpen} transparent={true}>
        <ImageViewer
          imageUrls={postImages}
          enableSwipeDown
          onCancel={() => setIsImageModalOpen(false)}
          onClick={() => setIsImageModalOpen(false)}
        />
      </Modal>
      <ActionSheet
        ref={actionImage}
        options={[
          intl.formatMessage({ id: 'post.copy_link' }),
          intl.formatMessage({ id: 'post.gallery_mode' }),
          intl.formatMessage({ id: 'post.save_to_local' }),
          intl.formatMessage({ id: 'alert.cancel' }),
        ]}
        title={intl.formatMessage({ id: 'post.image' })}
        cancelButtonIndex={3}
        onPress={(index) => {
          handleImagePress(index);
        }}
      />
      <ActionSheet
        ref={actionLink}
        options={[
          intl.formatMessage({ id: 'post.copy_link' }),
          intl.formatMessage({ id: 'alert.external_link' }),
          intl.formatMessage({ id: 'alert.cancel' }),
        ]}
        title={intl.formatMessage({ id: 'post.link' })}
        cancelButtonIndex={2}
        onPress={(index) => {
          handleLinkPress(index);
        }}
      />
      {revealComment ? (
        <AutoHeightWebView
          key={`akey-${created.toString()}`}
          source={{ html }}
          allowsFullscreenVideo={true}
          style={{ width: WIDTH - (32 + 34 * (commentDepth % 6)) }}
          customStyle={customStyle}
          onMessage={__handleOnLinkPress}
          customScript={customCommentScript}
          renderLoading={() => <CommentPlaceHolder />}
          startInLoadingState={true}
          onShouldStartLoadWithRequest={false}
          scrollEnabled={false}
          scalesPageToFit={false}
          zoomable={false}
        />
      ) : (
        <TextButton
          style={styles.revealButton}
          textStyle={styles.revealText}
          onPress={() => _showLowComment()}
          text={intl.formatMessage({ id: 'comments.reveal_comment' })}
        />
      )}
    </Fragment>
  );
};

const areEqual = (prevProps, nextProps) => prevProps.body !== nextProps.body;

const mapStateToProps = (state) => ({});

export default connect(mapStateToProps)(React.memo(CommentBody, areEqual));
